#!/bin/sh

# cleanup
rm -rf obj_dir
rm -f write_back.vcd

# run Verilator to translate Verilog into C++, including c++ testbench
verilator -Wall --cc --trace write_back.sv --exe write_back_tb.cpp

# build a c++ project via make automatically generated by Verilator
make -j -C obj_dir/ -f Vwrite_back.mk Vwrite_back

# run executable simulation file 
obj_dir/Vwrite_back