#!/bin/sh

# cleanup
rm -rf obj_dir
rm -f hazard_test.vcd

# run Verilator to translate Verilog into C++, including c++ testbench
verilator -Wall --cc --trace hazard_test.sv --exe hazard_tb.cpp

# build a c++ project via make automatically generated by Verilator
make -j -C obj_dir/ -f Vhazard_test.mk Vhazard_test

# run executable simulation file 
obj_dir/Vhazard_test